import './App.css';
import Die from "./components/die"

function App() {

	function allNewDice()
	{
		let newArray = []
		for (let index = 0; index < 10; index++) {
			newArray.push(Math.floor(Math.random() * 6 + 1));
		}
		return newArray
	}

	const dicesValue = allNewDice()
	dices = dicesValue.map(dice => 
		<Die value={dice}></Die>
		)
	return (
		<main className="App">
