import './App.css';
import React from "react"
import Die from "./components/die"

function App() {
	const [dice, setDice] = React.useState(() => allNewDice())
	function allNewDice()
	{
		let newArray = []
		for (let index = 0; index < 10; index++) {
			newArray.push({
					key : index,
					value : Math.floor(Math.random() * 6 + 1),
					isHeld : true
			})
		}
		return newArray
	}

	function rollDice ()
	{
		setDice(allNewDice)
	}

	function toggleDieState() {
		setDice(oldDice => ({...oldDice, isHeld : !oldDice.isHeld}))
	}
	const diceElements = dice.map(die => 
		<Die key={die.key} die={die} toggle={toggleDieState}></Die>
		)  
	return (
		<main className="App">
			<div className="dice-container">
				{diceElements}
			</div>
			<button className="roll-dice" onClick={rollDice}>Roll dice</button>
		</main>
  );
}

export default App;
